5.1 Parameter Store Access
// Configuration for AWS Parameter Store access
const parameterStorePolicy = {
  Version: "2012-10-17",
  Statement: [
    {
      Effect: "Allow",
      Action: [
        "ssm:GetParameter",
        "ssm:GetParameters"
      ],
      Resource: [
        "arn:aws:ssm:region:account-id:parameter/auth/*"
      ]
    }
  ]
}
